	public static String getEmailContent(String emailContent) {
		Document doc = Jsoup.parse(emailContent);
		int size = doc.select("html").size();
		if (size > 0) {
			//LOẠI BỎ THẺ HEAD
			StringBuilder result = new StringBuilder();
			if (doc.select("head").size() > 0) {
				result.append(doc.select("head").first().html());
			}
			//HỎI BỎ THẺ BODY
			if (doc.select("body").size() > 0) {
				Element body = doc.select("body").first();
				result.append(body.html());
			}
			return result.toString();
		}
		return emailContent;
	}
